import React from "react";

import { FlotChart } from "../../../common/graphs/flot";
import {
  Stats,
  BigBreadcrumbs,
  WidgetGrid,
  JarvisWidget,
  getJSON
} from "../../../common";

export default class FlotCharts extends React.Component {
  state = {};

  componentDidMount = () => {
    getJSON("assets/api/graphs/flot.json").then(data => {
      this.setState(data);
    });

    this.interval = setInterval(this.tick, 1000);
  };

  tick = () => {
    this.setState({
      updatingData: [FakeDataSource.getRandomData()]
    });
  };

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  render() {
    return (
      <div id="content">
        <div className="row">
          <BigBreadcrumbs
            items={["Flot Charts"]}
            icon="fa fa-fw fa-bar-chart-o"
            className="col-xs-12 col-sm-7 col-md-7 col-lg-4"
          />
          <Stats />
        </div>

        <WidgetGrid>
          <div className="row">
            <article className="col-sm-12 col-md-12 col-lg-12">
              <JarvisWidget id="wid-id-0" editbutton={false}>
                <header>
                  <span className="widget-icon">
                    <i className="fa fa-bar-chart-o" />
                  </span>

                  <h2>Bar Chart</h2>
                </header>
                <div>
                  <div className="widget-body no-padding">
                    <flot-bar-chart data="barChartData" />
                    <FlotChart
                      data={this.state.barChartData}
                      options={barChartDemoOptions}
                    />
                  </div>
                </div>
              </JarvisWidget>

              <JarvisWidget id="wid-id-1" editbutton={false}>
                <header>
                  <span className="widget-icon">
                    <i className="fa fa-bar-chart-o" />
                  </span>

                  <h2>Sin Chart</h2>
                </header>
                <div>
                  <div className="widget-body no-padding">
                    <FlotChart
                      data={this.state.sinChartData}
                      options={sinChartDemoOptions}
                    />
                  </div>
                </div>
              </JarvisWidget>
            </article>

            <article className="col-xs-12 col-sm-6 col-md-6 col-lg-6">
              <JarvisWidget id="wid-id-2" editbutton={false}>
                <header>
                  <span className="widget-icon">
                    <i className="fa fa-bar-chart-o" />
                  </span>
                  <h2>Auto Updating Chart</h2>
                </header>
                <div>
                  <div className="widget-body no-padding">
                    <FlotChart
                      data={this.state.updatingData}
                      options={autoUpdatingChartDemoOptions}
                    />
                  </div>
                </div>
              </JarvisWidget>
            </article>

            <article className="col-xs-12 col-sm-6 col-md-6 col-lg-6">
              <JarvisWidget id="wid-id-3" editbutton={false}>
                <header>
                  <span className="widget-icon">
                    <i className="fa fa-bar-chart-o" />
                  </span>
                  <h2>Horizontal Bar Chart</h2>
                </header>
                <div>
                  <div className="widget-body no-padding">
                    <FlotChart
                      data={this.state.horizontalBarChartData}
                      options={horizontalChartDemoOptions}
                    />
                  </div>
                </div>
              </JarvisWidget>
            </article>

            <article className="col-xs-12 col-sm-8 col-md-7 col-lg-7">
              <JarvisWidget id="wid-id-5" editbutton={false}>
                <header>
                  <span className="widget-icon">
                    <i className="fa fa-bar-chart-o" />
                  </span>

                  <h2>Plot Percentiles</h2>
                </header>
                <div>
                  <div className="widget-body no-padding">
                    <FlotChart
                      data={this.state.fillChartData}
                      options={fillChartDemoOptions}
                    />
                  </div>
                </div>
              </JarvisWidget>
            </article>

            <article className="col-xs-12 col-sm-4 col-md-5 col-lg-5">
              <JarvisWidget id="wid-id-6" editbutton={false}>
                <header>
                  <span className="widget-icon">
                    <i className="fa fa-bar-chart-o" />
                  </span>

