import React from "react";
import ReactDOM from "react-dom";

import $ from "jquery";
import "smartadmin-plugins/es6/jquery-ui.min";

$.widget(
  "ui.dialog",
  $.extend({}, $.ui.dialog.prototype, {
    _title: function(title) {
      if (!this.options.title) {
        title.html("&#160;");
      } else {
        title.html(this.options.title);
      }
    }
  })
);

export default class UiDialogLauncher extends React.Component {
  openDialog = e => {
    e.preventDefault();
    let $dialog = $("<div>").dialog({
      title: `<div class="widget-header">${this.props.header}</div>`,
      width: 400,
      modal: !!this.props.modal,
      close: function(e) {
        ReactDOM.unmountComponentAtNode(this);
        $(this).remove();
      }
    });
    let closeDialog = function(e) {
      e.preventDefault();
      $dialog.dialog("close");
    };
    let _content = this.props.content;
    let content = React.createElement(_content.type, {
      closeDialog: closeDialog
    });
    ReactDOM.render(content, $dialog[0]);
  };
  render() {
    return (
      <button onClick={this.openDialog} className={this.props.className}>
        {this.props.children}
      </button>
    );
  }
}
