import React from "react";
import { connect } from "react-redux";
import {
  Dropdown,
  MenuItem,
  OverlayTrigger,
  Tooltip,
  ProgressBar
} from "react-bootstrap";
import { Redirect, Route, Switch, Link } from "react-router-dom";
import Loadable from "react-loadable";
import { Loading } from "../../../common/navigation";

import { outlookInit } from "../OutlookActions";

const OutlookFolder = Loadable({
  loader: () => import("./OutlookFolder"),
  loading: Loading
});

const OutlookDetail = Loadable({
  loader: () => import("./OutlookDetail"),
  loading: Loading
});

const OutlookReplay = Loadable({
  loader: () => import("./OutlookReplay"),
  loading: Loading
});

const OutlookCompose = Loadable({
  loader: () => import("./OutlookCompose"),
  loading: Loading
});

class Outlook extends React.Component {
  componentWillMount() {
    this.props.dispatch(outlookInit());
  }

  render() {
    return (
      <div id="content">
        <div className="inbox-nav-bar no-content-padding">
          <h1 className="page-title txt-color-blueDark hidden-tablet">
            <i className="fa fa-fw fa-inbox" /> Outlook
          </h1>

          <Dropdown
            className="btn-group visible-tablet"
            id="inbox-select-folder-dropdown"
          >
            <Dropdown.Toggle className="btn btn-default dropdown-toggle">
              {this.props.folder.name}
            </Dropdown.Toggle>
            <Dropdown.Menu className="dropdown-menu pull-left">
              {this.props.folders.map(_folder => {
                let check =
                  _folder.key === this.props.match.params.folder ? (
                    <i className="fa fa-check" />
                  ) : null;
                return (
                  <MenuItem
                    key={_folder.key}
                    href={"#/outlook/folder/" + _folder.key}
                  >
                    {_folder.name} {check}
                  </MenuItem>
                );
              })}
            </Dropdown.Menu>
          </Dropdown>

          <div className="inbox-checkbox-triggered">
            <div className="btn-group">
              <OverlayTrigger
                placement="bottom"
                overlay={
                  <Tooltip id="mark-important-tooltip">Mark Important</Tooltip>
                }
              >
                <a href="#/" className="btn btn-default">
                  <strong>
                    <i className="fa fa-exclamation fa-lg text-danger" />
                  </strong>
                </a>
              </OverlayTrigger>

              <OverlayTrigger
                placement="bottom"
                overlay={
                  <Tooltip id="move-to-folder-tooltip">Move to folder</Tooltip>
                }
              >
                <a href="#/" className="btn btn-default">
                  <strong>
                    <i className="fa fa-folder-open fa-lg" />
                  </strong>
                </a>
              </OverlayTrigger>

              <OverlayTrigger
                placement="bottom"
                overlay={<Tooltip id="delete-message-tooltip">Delete</Tooltip>}
              >
                <a
                  href="deleteSelected()"
                  className="deletebutton btn btn-default"
                >
                  <strong>
                    <i className="fa fa-trash-o fa-lg" />
                  </strong>
                </a>
              </OverlayTrigger>
            </div>
          </div>

          <a href="#/"
            id="compose-mail-mini"
            className="btn btn-primary pull-right hidden-desktop visible-tablet"
          >
            <strong>
              <i className="fa fa-file fa-lg" />
            </strong>{" "}
          </a>

          <div className="btn-group pull-right inbox-paging">
            <a href="#/" className="btn btn-default btn-sm">
              <strong>
                <i className="fa fa-chevron-left" />
              </strong>
            </a>
            <a href="#/" className="btn btn-default btn-sm">
              <strong>
                <i className="fa fa-chevron-right" />
              </strong>
            </a>
          </div>
          <span className="pull-right">
            <strong>1-30</strong> of <strong>{this.props.folder.total}</strong>
          </span>
        </div>

        <div id="inbox-content" className="inbox-body no-content-padding">
          <div className="inbox-side-bar">
            <Link
              to="/outlook/compose"
              id="compose-mail"
              className="btn btn-primary btn-block"
            >
              <strong>Compose</strong>
            </Link>
            <h6>
              {" "}
              Folder
              <OverlayTrigger
                placement="right"
                overlay={<Tooltip id="inbox-refresh-tooltip">Refresh</Tooltip>}
              >
                <a href="#/" className="pull-right txt-color-darken">
                  <i className="fa fa-refresh" />
                </a>
              </OverlayTrigger>
            </h6>

            <ul className="inbox-menu-lg">
              {this.props.folders.map((_folder, idx) => {
                let unread = _folder.unread ? (
                  <span>({_folder.unread})</span>
                ) : null;
                return (
                  <li key={"inbox-sidebar-folder-" + idx}>
                    <Link to={"/outlook/folder/" + _folder.key}>
                      {" "}
                      {_folder.name} {unread}
                    </Link>
                  </li>
                );
              })}
            </ul>

            <h6>
              {" "}
              Quick Access
              <OverlayTrigger
                placement="right"
                overlay={<Tooltip id="add-label-tooltip">Add Another</Tooltip>}
              >
                <a href="#/" className="pull-right txt-color-darken">
                  <i className="fa fa-plus" />
                </a>
              </OverlayTrigger>
            </h6>

            <ul className="inbox-menu-sm">
              {this.props.folder.labels.map((label, idx) => {
                return (
                  <li key={"folder-label" + idx}>
                    <a href="#/">
                      {label.label} ({label.count})
                    </a>
                  </li>
                );
              })}
            </ul>

            <div className="air air-bottom inbox-space">
              {this.props.space.free} of{" "}
              <strong>{this.props.space.total}</strong>
              <OverlayTrigger
                placement="top"
                overlay={<Tooltip id="empty-spam-tooltip">Empty Spam</Tooltip>}
              >
                <a href="#/" className="pull-right txt-color-darken">
                  <i className="fa fa-trash-o fa-lg" />
                </a>
              </OverlayTrigger>
              <ProgressBar
                bsStyle="success"
                className="progress-micro"
                now={this.props.space.ratio}
              />
            </div>
          </div>

          <Switch>
            <Route path="/outlook/compose" component={OutlookCompose} />
            <Route path="/outlook/replay/:id" component={OutlookReplay} />
            <Route path="/outlook/detail/:id" component={OutlookDetail} />
            <Route path="/outlook/folder/:folder" component={OutlookFolder} />

            <Redirect from="/outlook" exact={true} to="/outlook/folder/inbox" />
          </Switch>

          <div className="inbox-footer">
            <div className="row">
              <div className="col-xs-6 col-sm-1">
                <div className="txt-color-white hidden-desktop visible-mobile">
                  {this.props.space.free} of{" "}
                  <strong>{this.props.space.total}</strong>
                  <ProgressBar
                    bsStyle="success"
                    className="progress-micro"
                    now={this.props.space.ratio}
                  />
                </div>
              </div>

              <div className="col-xs-6 col-sm-11 text-right">
                <div className="txt-color-white inline-block">
                  <i className="txt-color-blueLight hidden-mobile">
                    Last account activity <i className="fa fa-clock-o" /> 52
                    mins ago |
                  </i>{" "}
                  Displaying <strong>44 of 259</strong>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default connect(state => state.outlook)(Outlook);
